    if [[ -x "${BREW_PREFIX}/bin/bash" ]]; then
        SHELL="${BREW_PREFIX}/bin/bash";
        export SHELL;
    fi

    # BASH Completion ----------------------------------------------------------
    # shellcheck source=/dev/null
    [[ -f "${BREW_PREFIX}/etc/bash_completion" ]] && source "${BREW_PREFIX}/etc/bash_completion";
fi

# iTerm Integration ------------------------------------------------------------
